    public String DetectKey(java.awt.event.KeyEvent evt){
        String filename = null;
        try{
            //System.out.println(evt.getKeyChar());
            //filename = "\\wav\\"+evt.getKeyChar()+".wav";
            filename = evt.getKeyChar()+".wav";
        }catch(Exception e){
            System.out.println("Event could not be detected");
        }
        return(filename);
    }
    public void Play(String filename)
	{
            try{
                SoundClipTest sound = new SoundClipTest();
                sound.PlaySound(filename);
            } catch(Exception e){
                System.out.println("exception in play()");
            }            
	}
    
    /*
        //make a new thread for each new keyPressed event
        //& play the corresponding sounds symultaniously
        //destroy every thread for the keyReleased event
    */
    
    private void formKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_formKeyPressed
        
        try{
            new Thread(DetectKey(evt)){
            public void run(){
                System.out.println("Thread: " + getName() + " running");
              Play("\\wav\\"+DetectKey(evt));
            }
          }.start();
       }catch(Exception e){
           System.out.println("Exception in creating Thread");
       
    }//GEN-LAST:event_formKeyPressed
}
    public Thread getThreadByName(String threadName) {
    for (Thread t : Thread.getAllStackTraces().keySet()) {
        if (t.getName().equals(threadName))
            return t;
    }
    return null;
}
    private void formKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_formKeyReleased
        System.out.println("key releassed "+evt.getKeyChar());
        System.out.println("closing Thread "+getThreadByName(evt.getKeyChar()+".wav"));
    }//GEN-LAST:event_formKeyReleased

